class Solver:
    """
    Counts the number of connected components in an undirected graph.

    The baseline used NetworkX; this version uses an in-house Union-Find
    (Disjoint-Set) data structure with path compression and union-by-size,
    eliminating heavy dependencies and Python-level overhead.
    """

    # Public entry point expected by the evaluation harness
    def solve(self, problem: Dict[str, Any], **kwargs) -> Dict[str, int]:
        try:
            n: int = int(problem.get("num_nodes", 0))
            edges: List[Tuple[int, int]] = problem.get("edges", [])

            # Trivial cases
            if n <= 1 or not edges:
                return {"number_connected_components": n}

            # -------- Union-Find implementation --------
            parent = list(range(n))
            size = [1] * n  # union by size for near-optimal complexity

            def find(x: int) -> int:
                # Path compression (iterative, avoids recursion overhead)
                while parent[x] != x:
                    parent[x] = parent[parent[x]]
                    x = parent[x]
                return x

            def union(a: int, b: int) -> None:
                ra, rb = find(a), find(b)
                if ra == rb:
                    return
                # Make 'ra' the larger tree
                if size[ra] < size[rb]:
                    ra, rb = rb, ra
                parent[rb] = ra
                size[ra] += size[rb]

            # Perform unions for each valid edge
            for u, v in edges:
                # Skip self-loops and out-of-range indices defensively
                if 0 <= u < n and 0 <= v < n and u != v:
                    union(u, v)

            # Count unique roots
            seen_root = [False] * n
            components = 0
            for node in range(n):
                root = find(node)
                if not seen_root[root]:
                    seen_root[root] = True
                    components += 1

            return {"number_connected_components": components}

        except Exception:
            # Any failure returns sentinel value as per specification
            return {"number_connected_components": -1}

